const LiveSplitClient = require('./client');

(async () => {
    const client = new LiveSplitClient('127.0.0.1:16834');
    client.on('error', (err) => { console.log('Error from event:', err) });
    try {
		await client.connect();
    } catch (err) {
        console.error('Error from try/catch:', err);
    }
    console.log('We can do things later! Status:', client.connected ? 'connected' : 'not connected');
})();